bool Template::validateJsonForCreation(const Json::Value &pJson, std::string &err)
{
    if(pJson.isMember("id"))
    {
        if(!validJsonOfField(0, "id", pJson["id"], err, true))
            return false;
    }
    if(pJson.isMember("shop_id"))
    {
        if(!validJsonOfField(1, "shop_id", pJson["shop_id"], err, true))
            return false;
    }
    else
    {
        err="The shop_id column cannot be null";
        return false;
    }
    if(pJson.isMember("create_time"))
    {
        if(!validJsonOfField(2, "create_time", pJson["create_time"], err, true))
            return false;
    }
    else
    {
        err="The create_time column cannot be null";
        return false;
    }
    if(pJson.isMember("update_time"))
    {
        if(!validJsonOfField(3, "update_time", pJson["update_time"], err, true))
            return false;
    }
    else
    {
        err="The update_time column cannot be null";
        return false;
    }
    if(pJson.isMember("template_name"))
    {
        if(!validJsonOfField(4, "template_name", pJson["template_name"], err, true))
            return false;
    }
    else
    {
        err="The template_name column cannot be null";
        return false;
    }
    if(pJson.isMember("template_html"))
    {
        if(!validJsonOfField(5, "template_html", pJson["template_html"], err, true))
            return false;
    }
    if(pJson.isMember("option"))
    {
        if(!validJsonOfField(6, "option", pJson["option"], err, true))
            return false;
    }
    if(pJson.isMember("is_delete"))
    {
        if(!validJsonOfField(7, "is_delete", pJson["is_delete"], err, true))
            return false;
    }
    if(pJson.isMember("remark"))
    {
        if(!validJsonOfField(8, "remark", pJson["remark"], err, true))
            return false;
    }
    if(pJson.isMember("admin_id"))
    {
        if(!validJsonOfField(9, "admin_id", pJson["admin_id"], err, true))
            return false;
    }
    return true;
}
